import { StyleSheet, Text, View, Image, TextInput, Button } from "react-native";
import React, { useState } from "react";
import * as Yup from "yup";
import { Formik } from "formik";
import { Divider } from "react-native-elements";
import validUrl from "valid-url";

const uploadPostSchema = Yup.object().shape({
  imageUrl: Yup.string().url().required("A URL is required"),
  caption: Yup.string().max(2200, "Caption has reached the characters limit"),
});

const PLACEHOLDER_IMG =
  "data:image/png;base64,";

export default function FormikPostUploader({ navigation }) {
  const [thumbnailUrl, setThumbnailUrl] = useState(PLACEHOLDER_IMG);
  return (
    <Formik
      initialValues={{ caption: "", imageUrl: "" }}
      onSubmit={(values) => {
        console.log(values);
        navigation.goBack();
      }}
      validationSchema={uploadPostSchema}
      validateOnMount={true}
    >
      {({
        handleBlur,
        handleChange,
        handleSubmit,
        values,
        errors,
        isValid,
      }) => (
        <>
          <View
            style={{
              marginVertical: 20,
              justifyContent: "space-between",
              flexDirection: "row",
            }}
          >
            <View>
              <Image
                source={{ uri: validUrl.isUri(thumbnailUrl) ? thumbnailUrl : PLACEHOLDER_IMG }}
                style={{ width: 100, height: 100 }}
              />
            </View>
            <View style={{ flex: 1, marginLeft: 15 }}>
              <TextInput
                style={{ color: "white", fontSize: 18 }}
                placeholder="Write a caption"
                placeholderColor="gray"
                onChangeText={handleChange("caption")}
                onBlur={handleBlur("caption")}
                value={values.caption}
                multiline={true}
              />
            </View>
          </View>
          <Divider width={1} orientation="vetical" />
          <TextInput
            onChange={(e) => setThumbnailUrl(e.nativeEvent.text)}
            style={{ color: "white" }}
            placeholder="Enter Image Url"
            placeholderColor="gray"
            onChangeText={handleChange("imageUrl")}
            onBlur={handleBlur("imageUrl")}
            value={values.imageUrl}
          />
          {errors.imageUrl && (
            <Text style={{ color: "red", fontSize: 10, paddingVertical: 10 }}>
              {errors.imageUrl}
            </Text>
          )}
          <Button onPress={handleSubmit} title="Share" disabled={!isValid} />
        </>
      )}
    </Formik>
  );
}

const styles = StyleSheet.create({});
